mapper.select:
select od.id as orderId, od.create_time, od.pay_method, od.real_pay_amount, od.post_amount, status.order_status, od.is_comment from orders od left join order_status status on od.id = status.order_id where od.user_id = #{userId, jdbcType=VARCHAR} and od.is_delete = 0
and status.order_status = #{orderStatus, jdbcType=INTEGER}
order by od.update_time

select od.id as orderId, od.create_time, od.pay_method, od.real_pay_amount, od.post_amount, status.order_status, item.item_id, item.item_name, item.item_img, item.item_spec_id, item.item_spec_name, item.buy_counts, item.price from orders od left join order_status status on od.id = status.order_id left join order_items item on od.id = status.order_id where od.user_id = #{userId, jdbcType=VARCHAR} and od.is_delete = 0
and status.order_status = #{orderStatus, jdbcType=INTEGER}
order by od.update_time

select count(1)
from orders ord
left join order_status status on ord.id = status.order_id
where ord.user_id = #{userId, jdbcType=VARCHAR}
  and status.order_status = #{orderStatus, jdbcType=INTEGER}

  and ord.is_comment = #{isComment, jdbcType=INTEGER}
